# BauMax Gesamt-System
class BauMaxSystem:
    def __init__(self):
        self.customers = []
        self.projects = []

    def add_customer(self, name, email):
        self.customers.append({'name': name, 'email': email})

    def get_summary(self):
        return f'System hat {len(self.customers)} Kunden'

# Test
